    var length: Double
    var width: Double

    init(length: Double, width: Double) {
        self.length = length
        self.width = width
    }

    func area() -> Double {
        return length * width
    }
}

// Function that takes any Shape and prints its area
func calculateArea(of shape: Shape) {
    print("The area is \(shape.area())")
}

// Example usage:
