**Note:** C is not typically used for web scraping due to its complexity, but with these libraries, it is possible.

### Prerequisites:
- Install libcurl and libxml2 on your system.
- Compile with appropriate flags, e.g., `-lcurl -lxml2`.

### Example Code:

```c
#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <curl/curl.h>
#include <libxml/HTMLparser.h>
#include <libxml/xpath.h>

// Structure to hold fetched data
struct MemoryStruct {
    char *memory;
    size_t size;
};

// Callback function for libcurl to write data
static size_t WriteMemoryCallback(void *contents, size_t size, size_t nmemb, void *userp) {
    size_t realsize = size * nmemb;
    struct MemoryStruct *mem = (struct MemoryStruct *)userp;

    char *ptr = realloc(mem->memory, mem->size + realsize + 1);
    if(ptr == NULL) {
        // Out of memory
        printf("Not enough memory (realloc returned NULL)\n");
        return 0;
    }

    mem->memory = ptr;
    memcpy(&(mem->memory[mem->size]), contents, realsize);
    mem->size += realsize;
    mem->memory[mem->size] = 0;

    return realsize;
}

int main() {
    CURL *curl;
    CURLcode res;
    struct MemoryStruct chunk;

    chunk.memory = malloc(1);  // will be grown as needed by realloc
    chunk.size = 0;

    curl_global_init(CURL_GLOBAL_ALL);
    curl = curl_easy_init();

    if(curl) {
        curl_easy_setopt(curl, CURLOPT_URL, "http://example.com"); // URL to scrape
        curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, WriteMemoryCallback);
        curl_easy_setopt(curl, CURLOPT_WRITEDATA, (void *)&chunk);
        res = curl_easy_perform(curl);

        if(res != CURLE_OK) {
            fprintf(stderr, "curl_easy_perform() failed: %s\n", curl_easy_strerror(res));
        } else {
            // Parse HTML content
            htmlDocPtr doc = htmlReadMemory(chunk.memory, chunk.size, NULL, NULL, HTML_PARSE_NOERROR | HTML_PARSE_NOWARNING);
            if (doc == NULL) {
                fprintf(stderr, "Failed to parse HTML\n");
            } else {
                // Create XPath context
                xmlXPathContextPtr xpathCtx = xmlXPathNewContext(doc);
                if(xpathCtx == NULL) {
                    fprintf(stderr, "Failed to create XPath context\n");
                } else {
                    // Example XPath to get all links
                    xmlXPathObjectPtr xpathObj = xmlXPathEvalExpression((xmlChar *)"//a/@href", xpathCtx);
                    if(xpathObj == NULL) {
                        fprintf(stderr, "Failed to evaluate XPath\n");
                    } else {
                        xmlNodeSetPtr nodes = xpathObj->nodesetval;
                        for(int i=0; i < nodes->nodeNr; i++) {
                            xmlChar *href = xmlNodeListGetString(doc, nodes->nodeTab[i], 1);
                            printf("Link: %s\n", (char *)href);
                            xmlFree(href);
                        }
                        xmlXPathFreeObject(xpathObj);
                    }
                    xmlXPathFreeContext(xpathCtx);
                }
                xmlFreeDoc(doc);
            }
        }
        curl_easy_cleanup(curl);
    }

    free(chunk.memory);
    curl_global_cleanup();

    return 0;
}
```

